package C2.class02;

public class VariableCreation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//1 way to create a variable
		byte b=10; //create a variable and assign value

		// 2 way to create variable
		int i; //create variable only, reserve a space for me, will store values later
		i=100; //assign the value
		
		//create a variable only once, then use it as many times want/need during the program
		
		//3 way to create a variable, for variables of same type, 'a string'
		//create a variable=declare a variable
		
		int num1, num2, num3;
		
		num1=100; //assign value or initialize variable
		num2=200;
		num3=300;
		
		System.out.println(num2);
		
		//? can I change a value or variable?
		num2=2000; //reassigning the value, as many times you want 
		
		System.out.println(num2);
		
		//1 variable can hold only 1 value per line
		
		boolean boo=true;
		boo=false;
		boo=true;
		System.out.println(boo);
			
	
		
		
	}

}
